// The sequence of triangle numbers is generated by adding the natural numbers. So the 7th triangle number would be 1 + 2 + 3 + 4 + 5 + 6 + 7 = 28. The first ten terms would be:

// 1, 3, 6, 10, 15, 21, 28, 36, 45, 55, ...

// Let us list the factors of the first seven triangle numbers:

//  1: 1
//  3: 1,3
//  6: 1,2,3,6
// 10: 1,2,5,10
// 15: 1,3,5,15
// 21: 1,3,7,21
// 28: 1,2,4,7,14,28
// We can see that 28 is the first triangle number to have over five divisors.

// What is the value of the first triangle number to have over five hundred divisors?

function getFactorsOf(number) {
  let factors = 0;
  for (let i = 1; i <= Math.sqrt(number); i++) {
    if (number % i === 0) {
      factors++;
    }
  }
  return factors * 2;
}

let found = false;
let counter = 1;
let triangleNumber = 1;

while (!found) {
  counter++;
  // Create the next triangle number by adding one to the counter and
  // plus the previous triangle number
  // 2 + 1 = 3
  // 3 + 3 = 6
  // 4 + 6 = 10
  // 5 + 10 = 15
  // ...
  triangleNumber = triangleNumber + counter;
  // console.log(counter, triangleNumber);
  if (getFactorsOf(triangleNumber) > 500) {
    console.log(triangleNumber);
    found = true;
  }
}
